import { useEffect, useState, useCallback } from 'react';
import API from '../services/api';

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState('');

  const fetchTasks = useCallback(async () => {
    setLoading(true);
    try {
      const { data } = await API.get('/tasks');
      setTasks(data);
    } catch (err) {
      console.error("Fetch error:", err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchTasks(); }, [fetchTasks]);

  const addTask = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post('/tasks', { title });
      setTasks([data, ...tasks]);
      setTitle('');
    } catch (err) { alert("Add failed"); }
  };

  const deleteTask = async (id) => {
    if (!window.confirm("Delete task?")) return;
    try {
      // Corrected URL: ensure no trailing slashes or double 'tasks'
      await API.delete(`/tasks/${id}`);
      setTasks(tasks.filter(t => t._id !== id));
    } catch (err) {
      console.error("Delete error:", err.response);
      alert(err.response?.data?.message || "Delete failed");
    }
  };

  const startEdit = (task) => {
    setEditingId(task._id);
    setEditTitle(task.title);
  };

  const updateTask = async (id) => {
    try {
      const { data } = await API.put(`/tasks/${id}`, { title: editTitle });
      setTasks(tasks.map(t => t._id === id ? data : t));
      setEditingId(null);
    } catch (err) { alert("Update failed"); }
  };

  const filteredTasks = tasks.filter(t => t.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="p-6 md:p-10 max-w-4xl mx-auto min-h-screen bg-gray-50">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-blue-600">TaskPulse</h1>
        <button onClick={() => { localStorage.clear(); window.location.href='/login'; }} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
      </div>

      <input className="w-full border p-3 mb-6 rounded shadow-sm" placeholder="Search tasks..." value={search} onChange={(e) => setSearch(e.target.value)} />

      <form onSubmit={addTask} className="flex gap-4 mb-8">
        <input className="flex-grow border p-3 rounded" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="New task..." required />
        <button className="bg-green-600 text-white px-6 py-2 rounded font-bold">Add</button>
      </form>

      {loading ? <p className="text-center">Loading...</p> : (
        <div className="grid gap-3">
          {filteredTasks.map(task => (
            <div key={task._id} className="flex justify-between items-center bg-white p-4 rounded shadow-sm border">
              {editingId === task._id ? (
                <div className="flex gap-2 flex-grow mr-4">
                  <input className="border p-1 flex-grow" value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
                  <button onClick={() => updateTask(task._id)} className="text-green-600 font-bold">Save</button>
                  <button onClick={() => setEditingId(null)} className="text-gray-400">Cancel</button>
                </div>
              ) : (
                <>
                  <span className="text-lg">{task.title}</span>
                  <div className="flex gap-4">
                    <button onClick={() => startEdit(task)} className="text-blue-500 hover:underline">Edit</button>
                    <button onClick={() => deleteTask(task._id)} className="text-red-500 hover:underline">Delete</button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
